import axios from 'axios';
import React, {useState} from 'react';
import { Link, useNavigate } from 'react-router-dom';


export default function EditUser() {

    let navigate=useNavigate()
    // Store information parsed here

    const [user,setUser]=useState({
        name:"",
        username:"",
        email:"",
    })

    const{name, username, email}=user

    const onInputChange=(e)=>{
        // the "...user, []" syntax we'll keep on adding the values
        setUser({...user,[e.target.name]:e.target.value})
    }

    const onSubmit= async (e)=> {
        // if axios link doesn't exist, don't go anywhere. just freeze.
        e.preventDefault();
        // take the data stored in the state of the variable 'user' and send it over to the db link
        await axios.put("http://localhost:8080/user", user)    
        // navigate back home once that is done.
        navigate("/")  
    }

  return (
    <div className='container'>
        <div className='row'>
            <div className='col-md-6 offset-md-3 border rounded p-4 mt-2 shadow'>
                <h2 className='text-center m-4'>Edit user</h2>

                <form onSubmit={(e)=>onSubmit(e)}>
                <div className='mb-3'>
                    <label htmlFor='Name' className='form-label'>
                        Name
                    </label>
                    <input type={"text"} className="form-control" placeholder='Enter your name' name="name" value={name} onChange={(e)=>onInputChange(e)}></input>
                </div>
                 <div className='mb-3'>
                    <label htmlFor='Username' className='form-label'>
                        Username
                    </label>
                    <input type={"text"} className="form-control" placeholder='Enter your username' name="username" value={username} onChange={(e)=>onInputChange(e)}></input>
                </div>
                 <div className='mb-3'>
                    <label htmlFor='Email' className='form-label'>
                        Email
                    </label>
                    <input type={"text"} className="form-control" placeholder='Enter your email' name="email" value={email} onChange={(e)=>onInputChange(e)}></input>
                </div>
                    <button type="submit" className='btn btn-outline-primary'>Submit</button>
                    <Link className='btn btn-outline-danger mx-2' to="/">Cancel</Link>
                </form>
            </div>

        </div>
    </div>
  )
}
